Container.ClientEvent:
self.clickProxy = function(c, index) {
	if (self.current === c) {
		return;
	}
	var current = self.current;
	var temp = current.get("userData");
	current.set("userData", current.get("exClassName"));
	current.set("exClassName", temp);

	temp = c.get("userData");
	c.set("userData", c.get("exClassName"));
	c.set("exClassName", temp);

	self.current = c;

	cardBookChat.set("currentIndex", index);
}

DataListBox.ClientEvent:
$(arg.dom).empty();
var group = dsActiveGroup.getData("#");
var my = arg.data.get("sender") === "${loginUsername}";
var name = "";
var tip = new dorado.widget.Tip({
	content: arg.data.get("content").replace(/\n/g, "<br>"),
	floating: false,
	arrowDirection: (my ? "right" : "left") + (group.get("memberCount") > 2 ? " group" : ""),
	arrowAlign: "top"
});
var avatar = new dorado.widget.Control({
	exClassName: "message-avatar " + (my ? "right" : "left")
});

if (my) {
	name = "${loginUser.nickname}";
} else {
	name = arg.data.get("senderGroupMember.nickname");
}
$(avatar.getDom()).text(name[0].toLocaleUpperCase());

if (group.get("memberCount") > 2) {
	var nickname = new dorado.widget.Control({
		exClassName: "message-nickname " + (my ? "right" : "left")
	});
	$(nickname.getDom()).text(name);
	nickname.render(arg.dom);
}

avatar.render(arg.dom);
tip.render(arg.dom);

TextArea.ClientEvent:
var content = self.get("value");
if (arg.keyCode === 13 && content && !arg.shiftKey) {
	var group = dsActiveGroup.getData("#");
	if (!group) {
		return;
	} 
	Chat.send({
		type: "message",
		sender: "${loginUsername}",
		groupId: group.get("id"),
		all: group.get("all"),
		content: content,
		senderGroupMember: {
			groupId: group.get("id"),
			memberId: "${loginUsername}",
			nickname: "${loginUser.nickname}"
		}
	});
	self.set("value", null);
	arg.returnValue = false;
}

var group = dsActiveGroup.getData("#");
if (group && group.noticeCount > 0) {
	Chat.send({
		type: "mark-read",
		sender: "${loginUsername}",
		groupId: group.get("id")
	})

}

