$(arg.dom).find(".selection-check").on("click", function() {
	if ($(this).hasClass("fa-check-circle")) {
		$(this).removeClass("fa-check-circle green-text").addClass("fa-circle-o");
		arg.data.member.remove();
	} else {
		$(this).removeClass("fa-circle-o").addClass("fa-check-circle green-text");
		arg.data.member = dsGroup.getData("#").createChild("members", {
			memberId: arg.data.get("username"),
			nickname: arg.data.get("nickname")
		});
	}
});
